func randomStride(
  from start: Int,
  to end: Int,
  by maxStep: Int,
  seed: Int
) -> UnfoldSequence<Int, Int> {
  var rng = RepeatableRandomNumberGenerator(seed: seed)
  return sequence(state: start, next: {
    $0 += Int.random(in: 1 ... maxStep, using: &rng)
    guard $0 < end else { return nil }
    return $0
  })
}


